import React from 'react';
import headerLogo from '../images/logo.svg';
import profileAddButton from '../images/add-button.svg';

function App() {
  return (
    <>

      <label className="form__field">
        <input id="name-input" className="form__input form__input_type_name" name="name" type="text"
          minLength="2" maxLength="40" required />
        <span className="form__error name-input-error"></span>
      </label>
      <label className="form__field">
        <input id="about-input" className="form__input form__input_type_about" name="about" type="text"
          minLength="2" maxLength="200" required />
        <span className="form__error about-input-error"></span>
      </label>
      <div className="popup popup_show-image">
        <figure className="popup__block">
          <button className="popup__close popup__close_type_show-image" type="button" aria-label="Закрыть"></button>
          <img className="popup__image" alt=" " src="#" />
          <figcaption className="popup__caption"></figcaption>
        </figure>
      </div>
    </>
  );
}

export default App;
