import React from 'react';
import profileAddButton from '../images/add-button.svg';


function Main(props) {

    function handleEditAvatarClick() {
        document.querySelector('.popup_edit-avatar').classList.add('popup_opened');
    }

    function handleEditProfileClick() {

    }

    function handleAddPlaceClick() {

    }


    return (
        <main className="content page__content">
            <section className="profile content__profile">
                <div className="profile__block">
                    <div className="avatar" onClick={handleEditAvatarClick}>
                        <div className="avatar__overlay"></div>
                        <img className="avatar__image" src=" " alt="Аватар пользователя" />
                    </div>
                    <div className="profile__info">
                        <h1 className="profile__name">Жак-Ив Кусто</h1>
                        <button className="profile__button" type="button" aria-label="Редактировать профиль"></button>
                        <p className="profile__about">Исследователь океана</p>
                    </div>
                </div>
                <button className="profile__add-button"><img src={profileAddButton} alt="Создать"
                    className="profile__add-button-img" /></button>
            </section>
            <section className="elements content__elements">
            </section>
            <div className="popup popup_edit-avatar">
                <div className="popup__container">
                    <button className="popup__close" type="button" aria-label="Закрыть"></button>
                    <form className="form" name="formEditAvatar" noValidate>
                        <h2 className="form__header">Обновить аватар</h2>
                        <label className="form__field form__field_under">
                            <input id="avatar-input" className="form__input form__input_type_avatar" name="link" type="url"
                                required />
                            <span className="form__error avatar-input-error"></span>
                        </label>
                        <button className="form__button" type="submit">Сохранить</button>
                    </form>
                </div>
            </div>
        </main>

    );
}

export default Main;
