import React from 'react';

function ImagePopup(props) {

    console.log(props.card.target);

    return (
        <div className={`popup popup_show-image ${props.card ? ('popup_opened') : ''}`}>
            <figure className="popup__block">
                <button className="popup__close" type="button" aria-label="Закрыть"></button>
                <img className="popup__image" alt={props.card.target.alt} src={props.card.target.src} />
                <figcaption className="popup__caption">{props.card.target.alt}</figcaption>
            </figure>
        </div>
    )
}

export default ImagePopup
