import React from 'react';
import ReactDom from 'react-dom';
import api from '../utils/api';


function Card(props) {

    const [cards, setCards] = React.useState([]);

    function handleClick() {
        props.onCardClick(props.card);
    }

    React.useEffect(() => {
        api.getCards()
            .then(res => {
                ReactDom.render((
                    <>
                        {res.map((card, i) => (
                            <article key={card._id} className="element" >
                                <img className="element__image" alt="Картинка пользователя" src={card.link} />
                                <button className="element__trash-btn" type="button" aria-label="Удалить"></button>
                                <div className="element__block">
                                    <h2 className="element__title">{card.name}</h2>
                                    <div className="like">
                                        <button className="like__button" type="button" aria-label="Нравится"></button>
                                        <span className="like__counter">{card.likes.length}</span>
                                    </div>
                                </div>
                            </article>
                        ))
                            (setCards(card))
                        }
                    </>
                ), document.getElementById('elements'))
            })
    })

    return (
        <section id="elements" className="elements content__elements">
        </section>
    )
}

export default Card;
