import React from 'react';

function Card(props) {

    function handleClick() {
        props.onCardClick(props.card);
    }

    return (
        <article className="element">
            <img className="element__image" onClick={props.onCardClick} alt="Картинка пользователя" src={card.link} />
            <button className="element__trash-btn" type="button" aria-label="Удалить"></button>
            <div className="element__block">
                <h2 className="element__title">{card.name}</h2>
                <div className="like">
                    <button className="like__button" type="button" aria-label="Нравится"></button>
                    <span className="like__counter">{card.likes.length}</span>
                </div>
            </div>
        </article>
    )
}

export default Card;
